import React, { useState } from "react";
import axios from 'axios';
import texts from '../../data/Home/home-text.json';

const RequestJobForm = () => {
  const [inputs, setInputs] = useState({
    name: "Milton",
    email: "eduardouozv@gmail.com",
    age: 4,
    fechaNac: "2017-04-01",
    horarioHijo: "07:00",
    telContacto: "0952349239",
    referencia: "Pablo"
  });

  const [isSending, setIsSending] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };

  const handleSubmit = (event) => {
    // setInputs({ ...inputs, "message": encodeURIComponent("Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message).replace(/%20/g, '+')})
    setInputs({ ...inputs, "message": "Buenas,\nMi nombre es "+inputs.message})
    // encodeURIComponent(inputs.message).replace(/%20/g, '+')
    axios.post('https://miennielanndie.edu.uy/helloworld.php')
    .then(response => console.log("POST resp:"+response) )
    .catch(error => {
        // element.parentElement.innerHTML = `Error: ${error.message}`;
        console.error('There was an error!', error);
    });
    event.preventDefault();
  };

  return (
    <section className="contact-us">
    <div className="container">
      <div className="row">
        <div className="col-md-8 section-heading">
          {/* <ContactFormFive /> */}
          {/* <h5 style={{ textAlign: 'left', fontStyle: 'italic', textDecoration: 'underline'}}>Agregar Nueva Solicitud </h5> */}
          <div 
            data-aos-delay={300}
            data-aos={"fade-right"}
            data-aos-duration={700} >
            <h1 className="activities-font">
              Post√∫late
            </h1>
            <h3 className="activities-font" >
              para los pr√≥ximos llamados laborales
            </h3>
          </div>
          <br />
          <br />
          <br />
          <br />
          {texts.postulateTxt1.map( (t, index) => 
            <p key={index}  className="content-font" style={{ fontSize: '15px', textAlign: 'left'}} >{t}</p>
            )}
          <br />
          <form
            // onSubmit={(event) => handleSubmit(event)}
            // name="contact-form"
            id="contact-form"
            action={"mailto:"+texts.emailRequestJob+"?subject=[Postulaci√≥n] "+inputs.name+"&body=Hola"}
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action={"mailto:"+text.email}
            method="POST"
            enctype="text/plain"
          >
            <div className="form-floating">
              <input
                type="text"
                name="name"
                className="form-control"
                id="name"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                value={inputs.name}
                onChange={handleInputChange}
              />
              <label htmlFor="name">Nombre</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="lastname"
                className="form-control"
                id="lastname"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                value={inputs.lastname}
                onChange={handleInputChange}
              />
              <label htmlFor="lastname">Apellido</label>
              <div className="help-block with-errors mt-20"></div>
            </div>

            <div className="form-floating">
              <input
                type="number"
                name="ci"
                className="form-control"
                id="ci"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.ci}
                onChange={handleInputChange}
              />
              <label htmlFor="ci">CI</label>
              <div className="help-block with-errors mt-20"></div>
            </div>

            <div className="form-floating">
              <input
                type="number"
                name="age"
                className="form-control"
                id="age"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.age}
                onChange={handleInputChange}
              />
              <label htmlFor="age">Edad</label>
              <div className="help-block with-errors mt-20"></div>
            </div>

            <div className="form-floating">
              <input
                type="date"
                name="fechaNac"
                className="form-control"
                id="fechaNac"
                placeholder="Your Subject"
                required="required"
                value={inputs.fechaNac}
                onChange={handleInputChange}
              />
              <label htmlFor="fechaNac">Fecha de nacimiento</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="address"
                className="form-control"
                id="address"
                placeholder="Your Subject"
                value={inputs.address}
                onChange={handleInputChange}
              />
              <label htmlFor="address">Direcci√≥n</label>
            </div>
            <div className="form-floating">
              <input
                type="tel"
                name="telContacto"
                className="form-control"
                id="telContacto"
                placeholder="Your Subject"
                required="required"
                value={inputs.telContacto}
                onChange={handleInputChange}
              />
              <label htmlFor="telContacto">Tel√©fono de contacto</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="referencia"
                className="form-control"
                id="referencia"
                placeholder="Your Subject"
                value={inputs.referencia}
                onChange={handleInputChange}
              />
              <label htmlFor="referencia">Persona o Instituci√≥n de Referencia</label>
            </div>
            <button type="submit" className="btn btn-color btn-circle">
            {/* <button type="submit" name="submit" className="btn btn-color btn-circle"> */}
              Enviar
            </button>
          </form>
        </div>
      </div>
    </div>
  </section>

  );
};

export default RequestJobForm;
