import React, { useState } from "react";
import axios from 'axios';
import texts from '../../data/Home/home-text.json';

const RequestJobForm = () => {
  const [inputs, setInputs] = useState({
    name: "Milton",
    email: "eduardouozv@gmail.com",
    lastname: "Magallanes",
    ci: 12341234,
    age: 44,
    fechaNac: "2017-04-01",
    address: "Av. Gonzalo Rámirez 1671",
    telContacto: "0952349239",
    referencia: "Pablo"
  });

  const [isSending, setIsSending] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };

  const handleSubmit = (event) => {
    setIsSending(true);
    // setInputs({ ...inputs, "message": encodeURIComponent("Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message).replace(/%20/g, '+')})
    // setInputs({ ...inputs, "message": "Buenas,\nMi nombre es "+inputs.message})
    // encodeURIComponent(inputs.message).replace(/%20/g, '+')
    // axios.post('https://miennielanndie.edu.uy/helloworld.php')
    // .then(response => console.log("POST resp:"+response) )
    // .catch(error => {
    //     // element.parentElement.innerHTML = `Error: ${error.message}`;
    //     console.error('There was an error!', error);
    // });
    let body = "Buenas,%0D%0AMe gustaría postularme.%0D%0A%0D%0AMis datos son:%0D%0A%0D%0A";
    body += "Nombre: "+inputs.name+"%0D%0A";
    body += "Apellido: "+inputs.lastname+"%0D%0A";
    body += "CI: "+inputs.ci+"%0D%0A";
    body += "Edad: "+inputs.age+"%0D%0A";
    body += "Domicilio: "+inputs.address+"%0D%0A";
    body += "Teléfono de contacto: "+inputs.telContacto+"%0D%0A";
    body += "Persona o Institución de Referencia: "+inputs.referencia+"%0D%0A";
    window.location = "mailto:"+texts.emailRequestJob+"?subject=[Postulación] "+inputs.name+" "+inputs.lastname+"&body="+body;
    event.preventDefault();
    setIsSending(false);
  };

  return (
    <section className="contact-us">
    <div className="container">
      <div className="row">
        <div className="col-md-8 section-heading">
          {/* <ContactFormFive /> */}
          {/* <h5 style={{ textAlign: 'left', fontStyle: 'italic', textDecoration: 'underline'}}>Agregar Nueva Solicitud </h5> */}
          <div 
            data-aos-delay={300}
            data-aos={"fade-right"}
            data-aos-duration={700} >
            <h1 className="title-font" style={{ color: "rgb(126 189 134)"}}>
              Postúlate
            </h1>
            <h3 className="title-font subtitle-font-size" style={{ color: "rgb(126 189 134)"}} >
              para los próximos llamados laborales
            </h3>
          </div>
          <br />
          <br />
          <br />
          <br />
          {texts.postulateTxt1.map( (t, index) => 
            <p key={index}  className="content-font" style={{ fontSize: '15px', textAlign: 'left'}} >{t}</p>
            )}
          <br />
          <form
            onSubmit={(event) => handleSubmit(event)}
            // name="contact-form"
            id="contact-form"
            action={""}
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action={"mailto:"+text.email}
            method="POST"
            enctype="text/plain"
          >
            <div className="form-floating">
              <input
                type="text"
                name="name"
                className="form-control"
                id="name"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                value={inputs.name}
                onChange={handleInputChange}
              />
              <label htmlFor="name">Nombre</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="lastname"
                className="form-control"
                id="lastname"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                value={inputs.lastname}
                onChange={handleInputChange}
              />
              <label htmlFor="lastname">Apellido</label>
              <div className="help-block with-errors mt-20"></div>
            </div>

            <div className="form-floating">
              <input
                type="number"
                name="ci"
                className="form-control"
                id="ci"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.ci}
                onChange={handleInputChange}
              />
              <label htmlFor="ci">CI</label>
              <div className="help-block with-errors mt-20"></div>
            </div>

            <div className="form-floating">
              <input
                type="number"
                name="age"
                className="form-control"
                id="age"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.age}
                onChange={handleInputChange}
              />
              <label htmlFor="age">Edad</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="address"
                className="form-control"
                id="address"
                placeholder="Your Subject"
                value={inputs.address}
                onChange={handleInputChange}
              />
              <label htmlFor="address">Dirección</label>
            </div>
            <div className="form-floating">
              <input
                type="tel"
                name="telContacto"
                className="form-control"
                id="telContacto"
                placeholder="Your Subject"
                required="required"
                value={inputs.telContacto}
                onChange={handleInputChange}
              />
              <label htmlFor="telContacto">Teléfono de contacto</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="referencia"
                className="form-control"
                id="referencia"
                placeholder="Your Subject"
                value={inputs.referencia}
                onChange={handleInputChange}
              />
              <label htmlFor="referencia">Persona o Institución de Referencia</label>
            </div>
            <button type="submit" className="btn btn-color btn-circle">
            {/* <button type="submit" name="submit" className="btn btn-color btn-circle"> */}
              Enviar
            </button>
          </form>
        </div>
      </div>
    </div>
  </section>

  );
};

export default RequestJobForm;
