import React from "react";
import Swiper from "react-id-swiper";
import 'swiper/swiper-bundle.css';
import SliderButtons from "../../../elements/SliderButtons/SliderButtons";
import imgGroom from "../../../assets/images/";

const TravelSlider = ({ data }) => {
  const params = {
    loop: false,
  };

  return (
    <section className="pt-0 pb-0">
      <div className="slider-bg flexslider">
        <ul className="slides">
          <Swiper {...params}>
            {data.map((slide) => (
              <li key={slide.id}>
                <div
                  className="slide-img"
                  style={{
                    background: `url(${
                      require("../../../assets/images/" + slide.image)
                    }) center center / cover scroll no-repeat`,
                  }}
                ></div>
                <div
                  className={
                    "hero-text-wrap " + (slide.bg ? "gradient-overlay-bg" : "")
                  }
                >
                  <div className="hero-text white-color">
                    <div className="container text-center">
                      <h3 className="white-color" style={{ fontFamily: '29LT Azer' }}>
                        {slide.tagline ? slide.tagline : ""}
                      </h3>
                      <h2 className="white-color" style={{ fontFamily: 'Pinyon Script' }}>
                        {slide.title}
                      </h2>
                      <img src={imgAprender} className="rounded-circle" alt="img-1" />
                      {slide.circleImage.length !== 0 ? ( slide.circleImage.map((image) => (
                        <img src={"../../assets/images/"+image} className="rounded-circle" alt="img-1" />
                      )) ) : (
                        ""
                      )}

                      {slide.slogan.length !== 0 ? ( slide.slogan.map((slogan) => (
                        <>
                          <span style={{ fontFamily: 'Pinyon Script', fontSize: 18, color: slogan.color }}>
                            {slogan.txt}
                          </span>
                          <span>,  </span>
                        </>
                      )) ) : (
                        ""
                      )}
                      {slide.buttons.length !== 0 ? (
                        <p className="text-center mt-30">
                          <SliderButtons buttons={slide.buttons} />
                        </p>
                      ) : (
                        ""
                      )}
                    </div>
                  </div>
                </div>
              </li>
            ))}
          </Swiper>
        </ul>
      </div>
    </section>
  );
};

export default TravelSlider;
