import React, { Component } from "react";
import Carousel from "react-simply-carousel";
import imgChildGarden from "../../assets/images/carousel/childGarden.png";
import imgChild from "../../assets/images/carousel/childs.png";

const imgStyle = { width: 530, height: 353 };

const ImageCarousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  return (
    <div>
      <Carousel
        updateOnItemClick
        containerProps={{
          style: {
            width: "100%",
            justifyContent: "space-between"
          }
        }}
        autoplay
        speed={1500}
        activeSlideIndex={activeSlide}
        activeSlideProps={{
          style: {
            background: "blue"
          }
        }}
        onRequestChange={setActiveSlide}
        forwardBtnProps={{
          children: ">",
          style: {
            width: 60,
            height: 60,
            minWidth: 60,
            alignSelf: "center"
          }
        }}
        backwardBtnProps={{
          children: "<",
          style: {
            width: 60,
            height: 60,
            minWidth: 60,
            alignSelf: "center"
          }
        }}
        itemsToShow={2}
      >
        {Array.from({ length: 10 }).map((item, index) => (
          <div
            style={{
              background: "red",
              width: 300,
              height: 300,
              border: "30px solid white",
              textAlign: "center",
              lineHeight: "240px",
              boxSizing: "border-box"
            }}
            key={index}
          >
            {index}
          </div>
        ))}
      </Carousel>
    </div>
  );
}

  export default ImageCarousel;
