import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from 'axios';

import LoadingSpinner from '../KinderGarden/LoadingSpinner';
import texts from '../../data/Home/home-text.json';

const InscriptionForm = () => {
  const history = useHistory();
  const [inputs, setInputs] = useState({
    // name: "Testing",
    // email: "eduardouozv@gmail.com",
    // age: 4,
    // fechaNac: "2017-04-01",
    horarioHijo: "07:00",
    // telContacto: "0952349239",
    // horarioContacto: "22:00"
  });

  const [isSending, setIsSending] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };

  const handleSubmit = (event) => {
    setIsSending(true);
    // setInputs({ ...inputs, "message": encodeURIComponent("Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message).replace(/%20/g, '+')})
    // setInputs({ ...inputs, "message": "Buenas,\nMi nombre es "+inputs.message})
    // encodeURIComponent(inputs.message).replace(/%20/g, '+')
    // axios.post('https://miennielanndie.edu.uy/helloworld.php')
    // .then(response => console.log("POST resp:"+response) )
    // .catch(error => {
    //     // element.parentElement.innerHTML = `Error: ${error.message}`;
    //     console.error('There was an error!', error);
    // });
    let body = "Buenas,%0D%0AMe gustaría inscribirme.%0D%0A%0D%0AMis datos son:%0D%0A%0D%0A";
    body += "Nombre completo del niño/niña: "+inputs.name+"%0D%0A";
    body += "Edad: "+inputs.age+"%0D%0A";
    body += "Fecha de nacimiento: "+inputs.fechaNac+"%0D%0A";
    body += "Horario en el que deseas que concurra tu hijo/a: "+inputs.horarioHijo+"%0D%0A";
    body += "Teléfono al que quieres que te contactemos: "+inputs.telContacto+"%0D%0A";
    body += "Horario en el que prefieres que te contactemos: "+inputs.horarioContacto+"%0D%0A";
    body += "Mail donde quieres recibir nuestra información: "+inputs.email+"%0D%0A";
    window.location = "mailto:"+texts.emailInscriptions+"?subject=[Inscripción] "+inputs.name+"&body="+body;
    event.preventDefault();
    setIsSending(false);
  };

  return (
    <section className="contact-us">
    <div className="container">
      <div className="row">
        <div className="col-md-8 section-heading">
          {/* <ContactFormFive /> */}
          {/* <h5 style={{ textAlign: 'left', fontStyle: 'italic', textDecoration: 'underline'}}>Agregar Nueva Solicitud </h5> */}
          <h3
            className="title-font subtitle-font-size"
            data-aos-delay={300}
            data-aos={"fade-right"}
            data-aos-duration={700}
            style={{ textAlign: 'left', fontStyle: 'italic', textDecoration: 'underline', color: "rgb(23 90 194)" }}
          >
            Agregar Nueva Solicitud
          </h3>
          <br />
          <form
            onSubmit={(event) => handleSubmit(event)}
            // name="contact-form"
            id="contact-form"
            action={""}
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action={"mailto:"+text.email}
            method="POST"
            enctype="text/plain"
          >
            <div className="messages"></div>
            <div className="form-floating">
              <input
                type="text"
                name="name"
                className="form-control"
                id="name"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                onInvalid="this.setCustomValidity('Please Enter valid email')"
 onInput="setCustomValidity('')"
                value={inputs.name}
                onChange={handleInputChange}
              />
              <label htmlFor="name">Nombre del niño/niña</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="number"
                name="age"
                className="form-control"
                id="age"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.age}
                onChange={handleInputChange}
              />
              <label htmlFor="age">Edad</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="date"
                name="fechaNac"
                className="form-control"
                id="fechaNac"
                placeholder="Your Subject"
                required="required"
                value={inputs.fechaNac}
                onChange={handleInputChange}
              />
              <label htmlFor="fechaNac">Fecha de nacimiento</label>
            </div>
            <div className="form-floating">
              <input
                type="time"
                step="60"
                name="horarioHijo"
                className="form-control"
                id="horarioHijo"
                placeholder="Your Subject"
                value={inputs.horarioHijo}
                onChange={handleInputChange}
              />
              <label htmlFor="horarioHijo">Horario en el que deseas que concurra tu hijo/a</label>
            </div>
            <div className="form-floating">
              <input
                type="tel"
                name="telContacto"
                className="form-control"
                id="telContacto"
                placeholder="Your Subject"
                required="required"
                value={inputs.telContacto}
                onChange={handleInputChange}
              />
              <label htmlFor="telContacto">Teléfono de contacto</label>
            </div>
            <div className="form-floating">
              <input
                type="time"
                name="horarioContacto"
                className="form-control"
                id="horarioContacto"
                placeholder="Your Subject"
                value={inputs.horarioContacto}
                onChange={handleInputChange}
              />
              <label htmlFor="horarioContacto">Horario de contacto</label>
            </div>
            <div className="form-floating">
              <input
                type="email"
                name="email"
                className="form-control"
                id="email"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.email}
                onChange={handleInputChange}
              />
              <label htmlFor="email">Email de contacto</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            {!isSending ?
            <button type="submit" className="btn btn-color btn-circle">
              Enviar
            </button>
            : <LoadingSpinner />}
          </form>
        </div>
      </div>
    </div>
  </section>

  );
};

export default InscriptionForm;
