import React, { useState } from "react";
import axios from 'axios';
import texts from '../../data/Home/home-text.json';

const InscriptionForm = () => {
  const [inputs, setInputs] = useState({
    // name: "edu",
    // email: "eduardouozv@gmail.com",
    // subject: "asunto",
    // message: "Mensaje de relleno"
  });

  const [isSending, setIsSending] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };

  const handleSubmit = (event) => {
    // setInputs({ ...inputs, "message": encodeURIComponent("Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message).replace(/%20/g, '+')})
    setInputs({ ...inputs, "message": "Buenas,\nMi nombre es "+inputs.message})
    // encodeURIComponent(inputs.message).replace(/%20/g, '+')
    axios.post('https://miennielanndie.edu.uy/helloworld.php')
    .then(response => console.log("POST resp:"+response) )
    .catch(error => {
        // element.parentElement.innerHTML = `Error: ${error.message}`;
        console.error('There was an error!', error);
    });
    event.preventDefault();
  };

  return (
    <section className="contact-us">
    <div className="container">
      <div className="row">
        <div className="col-md-8 section-heading">
          {/* <ContactFormFive /> */}
          <form
            // onSubmit={(event) => handleSubmit(event)}
            // name="contact-form"
            id="contact-form"
            action={"mailto:"+texts.email+"?subject="+inputs.subject+"&body=Hola"}
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action="https://miennielanndie.edu.uy/helloworld.php"
            // action={"mailto:"+text.email}
            method="POST"
            enctype="text/plain"
          >
            <div className="messages"></div>
            <div className="form-floating">
              <input
                type="text"
                name="name"
                className="form-control"
                id="name"
                required="required"
                placeholder="Your Name"
                data-error="Your Name is Required"
                value={inputs.name}
                onChange={handleInputChange}
              />
              <label htmlFor="name">Nombre del niño/niña</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="email"
                name="email"
                className="form-control"
                id="email"
                placeholder="Your Email"
                required="required"
                data-error="Please Enter Valid Email"
                value={inputs.email}
                onChange={handleInputChange}
              />
              <label htmlFor="email">Edad</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="subject"
                className="form-control"
                id="subject"
                placeholder="Your Subject"
                value={inputs.subject}
                onChange={handleInputChange}
              />
              <label htmlFor="subject">Fecha de nacimiento</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="subject"
                className="form-control"
                id="subject"
                placeholder="Your Subject"
                value={inputs.subject}
                onChange={handleInputChange}
              />
              <label htmlFor="subject">Horario en el que deseas que concurra tu hijo/a</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="contactSchedule"
                className="form-control"
                id="subject"
                placeholder="Your Subject"
                value={inputs.subject}
                onChange={handleInputChange}
              />
              <label htmlFor="subject">Horario de contacto</label>
            </div>
            <div className="form-floating">
              <input
                type="text"
                name="subject"
                className="form-control"
                id="subject"
                placeholder="Your Subject"
                value={inputs.subject}
                onChange={handleInputChange}
              />
              <label htmlFor="subject">Asunto</label>
            </div>
            <div className="form-floating">
              <textarea
                name="message"
                className="form-control"
                id="message-input-simple"
                rows="7"
                placeholder="Your Message"
                required
                data-error="Please, Leave us a message"
                value={inputs.message}
                onChange={handleInputChange}
              ></textarea>
              <label htmlFor="message">Mensaje</label>
              <div className="help-block with-errors mt-20"></div>
            </div>
            <button type="submit" className="btn btn-color btn-circle">
            {/* <button type="submit" name="submit" className="btn btn-color btn-circle"> */}
              Enviar
            </button>
          </form>
        </div>
      </div>
    </div>
  </section>

  );
};

export default InscriptionForm;
