import React, { forwardRef } from "react";
import texts from "../../data/Home/home-text.json";

const AnimatedColumns = forwardRef(( { classAppend}, ref ) => {
  return (
    <section ref={ref} className="first-ico-box" id="about">
        <div className="container">
            <div className="row">
                <div
                className={"col-sm-8 section-heading " + (classAppend ? classAppend : "")}
                >
                <div className="row">
                    <div className="col-md-6 mb-20 col-sm-6" style={{ textAlign: 'left'}}>
                            {texts.activities.slice(0, 8).map( t => 
                                <p data-aos-delay={300}
                                    data-aos={"fade-up"}
                                    data-aos-duration={700}
                                    className="content-font mt-30"
                                    style={{ fontSize: '15px' }} >
                                    {"\u2022"+t}
                                </p>
                            )}

                    </div>
                    <div className="col-md-6 mb-20 col-sm-6" style={{ textAlign: 'left'}}>
                           {texts.activities.slice(-7).map( t => 
                                <p data-aos-delay={300}
                                data-aos={"fade-up"}
                                data-aos-duration={700}
                                className="content-font mt-30"
                                style={{ fontSize: '15px' }} >
                                {t}
                            </p>
                            )}
                    </div>
                    </div>
                </div>
            </div>
        </div>
    </section>
  );
});

export default AnimatedColumns;
