import React, { forwardRef } from "react";
import texts from "../../data/Home/home-text.json";

const renderBulletList = (list) => (
    <ul style={{ marginLeft: '20px', listStyle: "none", '&::before': { content: '✓'}, '&::after': {
        content: `''`,
        position: 'absolute',
        left: '-50px',
        top: '50px',
        width: '0',
        height: '0',
        border: '50px solid transparent',
        borderTopColor: 'red',
      } }}>
        {list.map( t => 
            <li data-aos-delay={300}
                data-aos={"fade-up"}
                data-aos-duration={700}
                className="content-font mt-30"
                style={{ fontSize: '15px', "&::after": {
                    content: `''`,
                    position: 'absolute',
                    left: '-50px',
                    top: '50px',
                    width: '0',
                    height: '0',
                    border: '50px solid transparent',
                    borderTopColor: 'red',
                  } }} >
                {t}
            </li>
        )}
    </ul>
);

const AnimatedColumns = forwardRef(( { classAppend} ) => {
  return (
        <div className="container">
            <div className="row">
                <div
                className={"col-sm-8 section-heading " + (classAppend ? classAppend : "")}
                >
                <div className="row">
                    <div className="col-md-6 mb-20 col-sm-6" style={{ textAlign: 'left'}}>
                        {renderBulletList(texts.activities.slice(0, 7))}

                    </div>
                    <div className="col-md-6 mb-20 col-sm-6" style={{ textAlign: 'left'}}>
                           {renderBulletList(texts.activities.slice(-7))}
                    </div>
                    </div>
                </div>
            </div>
        </div>
  );
});

export default AnimatedColumns;
