import React, { useState } from "react";
import text from '../../data/Home/home-text.json';

const ContactFormKinderGarden = () => {
  const [inputs, setInputs] = useState({
    name: "edu",
    email: "eduardouozv@gmail.com",
    subject: "asunto",
    message: "Mensaje de relleno y había una milanesa"
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: value });
  };

  const handleSubmit = (event) => {
    // setInputs({ ...inputs, "message": encodeURIComponent("Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message).replace(/%20/g, '+')})
    setInputs({ ...inputs, "message": "Buenas,\n&nbsp;%20Mi&nbsp;+++nombre es "+inputs.message})
    // encodeURIComponent(inputs.message).replace(/%20/g, '+')
    // event.preventDefault();
  };

  return (
    <form
      onSubmit={(event) => handleSubmit(event)}
      name="contact-form"
      id="contact-form"
      action={"mailto:"+text.email}
      method="POST"
      enctype="text/plain"
    >
      <div className="messages"></div>
      <div className="form-floating">
        <input
          type="text"
          name="name"
          className="form-control"
          id="name"
          required="required"
          placeholder="Your Name"
          data-error="Your Name is Required"
          value={inputs.name}
          onChange={handleInputChange}
        />
        <label htmlFor="name">Nombre</label>
        <div className="help-block with-errors mt-20"></div>
      </div>
      <div className="form-floating">
        <input
          type="email"
          name="email"
          className="form-control"
          id="email"
          placeholder="Your Email"
          required="required"
          data-error="Please Enter Valid Email"
          value={inputs.email}
          onChange={handleInputChange}
        />
        <label htmlFor="email">Email</label>
        <div className="help-block with-errors mt-20"></div>
      </div>
      <div className="form-floating">
        <input
          type="text"
          name="subject"
          className="form-control"
          id="subject"
          placeholder="Your Subject"
          value={inputs.subject}
          onChange={handleInputChange}
        />
        <label htmlFor="subject">Asunto</label>
      </div>
      <div className="form-floating">
        <textarea
          name="body"
          className="form-control"
          id="message-input-simple"
          rows="7"
          placeholder="Your Message"
          required
          data-error="Please, Leave us a message"
          value={inputs.message}
          onChange={handleInputChange}
        ></textarea>
        <label htmlFor="message">Mensaje</label>
        <div className="help-block with-errors mt-20"></div>
      </div>
      <button type="submit" name="submit" className="btn btn-color btn-circle">
        Enviar
      </button>
    </form>
  );
};

export default ContactFormKinderGarden;
