import React from "react";

const AnimatedSection = ({ title, tagline, classAppend, font, children }) => {
  return (
    <div
      className={"col-sm-8 section-heading " + (classAppend ? classAppend : "")}
    >
      <h1
        className="activities-font"
        data-aos-delay={100}
        data-aos={"fade-up"}
        data-aos-duration={700}
      >
        {title}
      </h1>
      {tagline && (
        <h4
          className={"text-uppercase " + (font ? font : "")}
          data-aos-delay={200}
          data-aos={"fade-up"}
          data-aos-duration={700}
        >
          {`- ${tagline} -`}
        </h4>
      )}
      {children ? (
        <p
          data-aos-delay={300}
          data-aos={"fade-up"}
          data-aos-duration={700}
          className="mt-30"
        >
          {children}
        </p>
      ) : null}
      <br />
      <br />
      <br />
      <br />
      <div className="row">
          <div className="col-md-6 mb-20 col-sm-6">
